import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import {
  Container,
  Typography,
  RadioGroup,
  Radio,
  FormControlLabel,
  Button,
  LinearProgress,
  Box,
} from "@mui/material";
interface Question {
  id: number;
  text: string;
  options: string[];
}

interface QuestionnaireData {
  id: number;
  title: string;
  questions: Question[];
}
interface LocationState {
  questionnaire: QuestionnaireData;
}
const questionnaireData: QuestionnaireData = {
  id: 1,
  title: "Customer Satisfaction Survey",
  questions: [
    {
      id: 1,
      text: "How satisfied are you with our service?",
      options: [
        "Very satisfied",
        "Satisfied",
        "Neutral",
        "Dissatisfied",
        "Very dissatisfied",
      ],
    },
    {
      id: 2,
      text: "How likely are you to recommend us to others?",
      options: [
        "Very likely",
        "Likely",
        "Neutral",
        "Unlikely",
        "Very unlikely",
      ],
    },
    // Add more questions as needed
  ],
};

export const Questionnaire: React.FC = () => {
  const location = useLocation();
  const state = location.state as LocationState;
  const questionnaire = location.state.questionnaire;
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState<Record<number, string>>({});
  const handleAnswerChange = (questionId: number, answer: string) => {
    setAnswers((prevAnswers) => ({
      ...prevAnswers,
      [questionId]: answer,
    }));
  };

  const handleNext = () => {
    if (currentQuestionIndex < questionnaireData.questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const handlePrevious = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  const currentQuestion = questionnaireData.questions[currentQuestionIndex];
  const progress =
    ((currentQuestionIndex + 1) / questionnaireData.questions.length) * 100;

  return (
    <Container>
      <Typography variant="h4">{questionnaireData.title}</Typography>
      <Box mt={4}>
        <Typography variant="h6">{currentQuestion.text}</Typography>
        <RadioGroup
          value={answers[currentQuestion.id] || ""}
          onChange={(e) =>
            handleAnswerChange(currentQuestion.id, e.target.value)
          }
        >
          {currentQuestion.options.map((option) => (
            <FormControlLabel
              key={option}
              value={option}
              control={<Radio />}
              label={option}
            />
          ))}
        </RadioGroup>
      </Box>
      <Box mt={4} display="flex" justifyContent="space-between">
        <Button
          variant="contained"
          onClick={handlePrevious}
          disabled={currentQuestionIndex === 0}
        >
          Previous
        </Button>
        <Button
          variant="contained"
          onClick={handleNext}
          disabled={
            currentQuestionIndex === questionnaireData.questions.length - 1
          }
        >
          Next
        </Button>
      </Box>
      <Box mt={4}>
        <LinearProgress variant="determinate" value={progress} />
        <Typography variant="body2" color="textSecondary">
          {`Progress: ${currentQuestionIndex + 1}/${
            questionnaireData.questions.length
          }`}
        </Typography>
      </Box>
    </Container>
  );
};
