import React, {
  createContext,
  useContext,
  useState,
  useEffect,
  ReactNode,
} from "react";

interface Question {
  id: number;
  text: string;
  options: string[];
}

interface Questionnaire {
  id: number;
  title: string;
  questions: Question[];
}

interface QuestionnaireContextType {
  questionnaires: Questionnaire[];
  loading: boolean;
  error: string | null;
}

const QuestionnaireContext = createContext<
  QuestionnaireContextType | undefined
>(undefined);

interface QuestionnaireProviderProps {
  children: ReactNode;
}

export const QuestionnaireProvider: React.FC<QuestionnaireProviderProps> = ({
  children,
}) => {
  const [questionnaires, setQuestionnaires] = useState<Questionnaire[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("/api/questionnaires");
        if (!response.ok) {
          throw new Error("Failed to fetch questionnaires");
        }
        const data = await response.json();
        setQuestionnaires(data);
      } catch (err: any) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  return (
    <QuestionnaireContext.Provider value={{ questionnaires, loading, error }}>
      {children}
    </QuestionnaireContext.Provider>
  );
};

export const useQuestionnaires = () => {
  const context = useContext(QuestionnaireContext);
  if (!context) {
    throw new Error(
      "useQuestionnaires must be used within a QuestionnaireProvider"
    );
  }
  return context;
};
