import Grid from "@mui/material/Grid2";
import styles from "./scss/Login.scss";
import CustomTextField from "../../shared/custom-text-field/CustomTextField";
import BlackButton from "../../shared/button/BlackButton";
import { Typography } from "@mui/material";
import { useUser } from "../../context/UserContext"; // Import useUser

export const Login = () => {
  const { login } = useUser(); // Get the login function from context

  const handleLoginClick = () => {
    // Normally, you'd get these values from the input fields
    const username = "a"; // Replace with actual username input value
    const password = "a"; // Replace with actual password input value

    // Replace this with actual authentication logic
    if (username && password) {
      const user = { id: "1", username }; // Mock user data
      login(user); // Set the user in context after a successful login
      console.log("User logged in:", user);
    } else {
      console.log("Login failed");
    }
  };

  return (
    <Grid container className={styles.pageContainer} id="page-container">
      <Grid
        container
        id="login-card"
        sx={{
          borderRadius: "5px",
          boxShadow: "0 0 5px 1px #e6e6e6",
          paddingBottom: "80px",
          maxWidth: "600px",
          justifyContent: "center",
          maxHeight: "450px",
        }}
      >
        <Grid
          container
          width={"80%"}
          sx={{
            justifyContent: "center",
            paddingTop: "40px",
            paddingLeft: "40px",
            paddingRight: "40px",
            rowGap: "3vh",
          }}
        >
          <Grid container width={"80%"}>
            <Typography variant={"h4"}>Welcome back</Typography>
          </Grid>
          <Grid container width={"100%"} justifyContent={"center"}>
            <CustomTextField sx={{ width: "80%" }} label={"Username"} />
          </Grid>
          <Grid container width={"100%"} justifyContent={"center"}>
            <CustomTextField sx={{ width: "80%" }} label={"Password"} />
          </Grid>
          <Grid container width={"100%"} justifyContent={"center"}>
            <BlackButton sx={{ width: "80%" }} onClick={handleLoginClick}>
              Login
            </BlackButton>
          </Grid>
        </Grid>
      </Grid>
    </Grid>
  );
};
