import React, { createContext, useState, useEffect } from "react";
import { QuestionnaireContextType } from "../types/QuestionnaireContextType";
import { QuestionnaireProviderProps } from "../types/QuestionnaireProviderProps";
import { APIClient } from "./APIC";
import { QuestionnaireType } from "../types/QuestionnaireType";

export const QuestionnaireContext = createContext<
  QuestionnaireContextType | undefined
>(undefined);

export const QuestionnaireContextProvider: React.FC<
  QuestionnaireProviderProps
> = ({ children }) => {
  const [questionnaires, setQuestionnaires] = useState<QuestionnaireType[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await new APIClient().fetchQuestionnaires();
        if (!response) {
          throw new Error("Failed to fetch questionnaires");
        }

        const data = await response.json();
        const questionnaires = data.map(
          (questionnaire: any) => questionnaire as QuestionnaireType
        );
        setQuestionnaires(questionnaires);
      } catch (err: any) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, []);
  return (
    <QuestionnaireContext.Provider value={{ questionnaires, loading, error }}>
      {children}
    </QuestionnaireContext.Provider>
  );
};
