import React, { createContext, useState, useEffect, ReactNode } from "react";

export interface Questionnaire {
  id: number;
  title: string;
  questions: { id: number; text: string; options: string[] }[];
}

interface QuestionnaireContextType {
  questionnaires: Questionnaire[];
  loading: boolean;
  error: string | null;
}

export const QuestionnaireContext = createContext<
  QuestionnaireContextType | undefined
>(undefined);

interface QuestionnaireProviderProps {
  children: ReactNode;
}

export const QuestionnaireProvider: React.FC<QuestionnaireProviderProps> = ({
  children,
}) => {
  const [questionnaires, setQuestionnaires] = useState<Questionnaire[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch("/api/questionnaires");
        if (!response.ok) {
          throw new Error("Failed to fetch questionnaires");
        }
        const data = await response.json();
        setQuestionnaires(data);
      } catch (err: any) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, []);

  return (
    <QuestionnaireContext.Provider value={{ questionnaires, loading, error }}>
      {children}
    </QuestionnaireContext.Provider>
  );
};
