import React, { useState } from "react";
import {
  Container,
  Typography,
  RadioGroup,
  Radio,
  FormControlLabel,
  Button,
  LinearProgress,
  Box,
} from "@mui/material";
import { useParams } from "react-router-dom";
import { useQuestionnaires } from "../questionnaire-context/hooks/useQuestionnaires";
interface Question {
  id: number;
  text: string;
  options: string[];
}

interface QuestionnaireData {
  id: number;
  title: string;
  questions: Question[];
}

export const Questionnaire: React.FC = () => {
  const { id } = useParams<{ id: string }>(); // Ensure useParams is correctly typed
  const questionnaires = useQuestionnaires();
  const questionnaire = questionnaires.questionnaires.find(
    (q) => q.id === Number(id)
  );
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState<Record<number, string>>({});
  const handleAnswerChange = (questionId: number, answer: string) => {
    setAnswers((prevAnswers) => ({
      ...prevAnswers,
      [questionId]: answer,
    }));
  };

  const handleNext = () => {
    if (currentQuestionIndex < questionnaire.questions.length ?? -1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const handlePrevious = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  const currentQuestion = questionnaire?.questions[currentQuestionIndex];
  const progress =
    ((currentQuestionIndex + 1) / questionnaireData.questions.length) * 100;

  return (
    <Container>
      <Typography variant="h4">{questionnaireData.title}</Typography>
      <Box mt={4}>
        <Typography variant="h6">{currentQuestion.text}</Typography>
        <RadioGroup
          value={answers[currentQuestion.id] || ""}
          onChange={(e) =>
            handleAnswerChange(currentQuestion.id, e.target.value)
          }
        >
          {currentQuestion.options.map((option) => (
            <FormControlLabel
              key={option}
              value={option}
              control={<Radio />}
              label={option}
            />
          ))}
        </RadioGroup>
      </Box>
      <Box mt={4} display="flex" justifyContent="space-between">
        <Button
          variant="contained"
          onClick={handlePrevious}
          disabled={currentQuestionIndex === 0}
        >
          Previous
        </Button>
        <Button
          variant="contained"
          onClick={handleNext}
          disabled={
            currentQuestionIndex === questionnaireData.questions.length - 1
          }
        >
          Next
        </Button>
      </Box>
      <Box mt={4}>
        <LinearProgress variant="determinate" value={progress} />
        <Typography variant="body2" color="textSecondary">
          {`Progress: ${currentQuestionIndex + 1}/${
            questionnaireData.questions.length
          }`}
        </Typography>
      </Box>
    </Container>
  );
};
