import React, { useEffect, useState } from "react";
import {
  Container,
  Typography,
  Checkbox,
  RadioGroup,
  Radio,
  FormControlLabel,
  Button,
  LinearProgress,
  Box,
  TextField,
} from "@mui/material";
import { useLocation, useParams } from "react-router-dom";
import { QuestionCategory } from "../../../types/enums/QuestionCategory";
import { APIClient } from "../../../helpers/APIC";
import { QuestionResponse } from "../../../helpers/QuestionResponse";
import { useUser } from "../../../context/UserContextProvider";
import { QuestionOption } from "../../../types/domains/QuestionOption";
import { useNavigate } from "react-router-dom";
import { useQuestionResponses } from "../../../hooks/useQuestionResponses";

export const Questionnaire: React.FC = () => {
  const navigate = useNavigate();

  // const questionnaires = useQuestionnaires();
  const userId = useUser().userId!;
  const prevQuestionResponses = useQuestionResponses();

  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState<QuestionResponse[]>([]);
  const [errors, setErrors] = useState<Record<number, string>>({});

  // Find the questionnaire using the ID from the URL after ensuring hooks are defined
  const location = useLocation();
  const questionnaire = location.state?.questionnaire;

  useEffect(() => {
    const questionId = questionnaire!.questions[currentQuestionIndex].id;
    for (let response of prevQuestionResponses.questionResponses) {
      if (response.questionId === questionId) {
        switch (response.type) {
          case QuestionCategory.MultipleChoice:
            setAnswers((prevAnswers) => [
              ...prevAnswers,
              {
                userId: userId,
                questionId: questionId,
                type: QuestionCategory.MultipleChoice,
                singleOptionId: response.singleOptionId,
                multiOptionIds: null,
                shortAnswer: null,
              },
            ]);
            break;
          case QuestionCategory.MultipleChoiceSelectAll:
            setAnswers((prevAnswers) => [
              ...prevAnswers,
              {
                userId: userId,
                questionId: questionId,
                type: QuestionCategory.MultipleChoice,
                singleOptionId: null,
                multiOptionIds: response.multiOptionIds,
                shortAnswer: null,
              },
            ]);
            break;
          case QuestionCategory.ShortAnswer:
            setAnswers((prevAnswers) => [
              ...prevAnswers,
              {
                userId: userId,
                questionId: questionId,
                type: QuestionCategory.MultipleChoice,
                singleOptionId: null,
                multiOptionIds: null,
                shortAnswer: response.shortAnswer,
              },
            ]);
            break;
        }
      }
    }
  }, [
    currentQuestionIndex,
    questionnaire,
    userId,
    prevQuestionResponses,
    navigate,
  ]);

  const handleAnswerChange = (
    questionId: number,
    answerType: QuestionCategory,
    singleOptionId: number | null,
    multiOptionId: number | null,
    shortAnswer: string | null
  ) => {
    setAnswers((prevAnswers) => {
      const answerIndex = prevAnswers.findIndex(
        (answer) => answer.questionId === questionId
      );

      // Handle multi select questions
      if (
        answerType === QuestionCategory.MultipleChoiceSelectAll &&
        multiOptionId !== null
      ) {
        // Update curr answer if it exists, else create a new answer
        if (
          answerIndex > -1 &&
          prevAnswers[answerIndex].multiOptionIds != null
        ) {
          for (let optionId of prevAnswers[answerIndex].multiOptionIds) {
            // If the option is already selected, remove it from the array of selected options
            if (optionId === multiOptionId) {
              prevAnswers[answerIndex].multiOptionIds = prevAnswers[
                answerIndex
              ].multiOptionIds.filter(
                (optionId: number) => optionId !== multiOptionId
              );
              return [
                ...prevAnswers.slice(0, answerIndex),
                prevAnswers[answerIndex],
                ...prevAnswers.slice(answerIndex + 1),
              ];
            }
          }
          // If the option is not already selected, add it to the array of selected options
          prevAnswers[answerIndex].multiOptionIds.push(multiOptionId);

          // Insert the updated answer into the answer array
        } else {
          let updatedMultiOptions: number[] = [];
          updatedMultiOptions.push(multiOptionId);
          // Add the new answer to the previous answers
          return [
            ...prevAnswers,
            {
              userId: userId,
              questionId: questionId,
              type: answerType,
              singleOptionId: null,
              multiOptionIds: updatedMultiOptions,
              shortAnswer: null,
            },
          ];
        }
      }

      // Handle single select questions
      else if (
        answerType === QuestionCategory.MultipleChoice &&
        singleOptionId != null
      ) {
        // If the answer already exists, update the singleOption value
        if (answerIndex > -1) {
          const updatedAnswer: QuestionResponse = {
            ...prevAnswers[answerIndex],
            singleOptionId: singleOptionId,
          };
          return [
            ...prevAnswers.slice(0, answerIndex),
            updatedAnswer,
            ...prevAnswers.slice(answerIndex + 1),
          ];
        } else {
          const updatedAnswer: QuestionResponse = {
            userId: userId,
            questionId: questionId,
            type: answerType,
            singleOptionId: singleOptionId,
            multiOptionIds: null,
            shortAnswer: null,
          };
          return [...prevAnswers, updatedAnswer];
        }
      } else if (
        answerType === QuestionCategory.ShortAnswer &&
        shortAnswer != null
      ) {
        // If the answer already exists, concatenate the new value with the existing one
        if (answerIndex > -1 && prevAnswers[answerIndex].shortAnswer != null) {
          prevAnswers[answerIndex].shortAnswer = shortAnswer;
          return [
            ...prevAnswers.slice(0, answerIndex),
            prevAnswers[answerIndex],
            ...prevAnswers.slice(answerIndex + 1),
          ];
        } else {
          const updatedAnswer: QuestionResponse = {
            userId: userId,
            questionId: questionId,
            type: answerType,
            singleOptionId: null,
            multiOptionIds: null,
            shortAnswer: shortAnswer,
          };
          return [...prevAnswers, updatedAnswer];
        }
      }
      return prevAnswers;
    });

    // Reset error if there was one
    if (errors[questionId])
      setErrors((prevErrors) => ({
        ...prevErrors,
        [questionId]: "",
      }));
  };

  // No empty answers or white space only answers
  const validateAnswer = (questionId: number) => {
    const answer = answers[questionId] || "";
    if (
      answer.type === QuestionCategory.ShortAnswer &&
      (answer.shortAnswer === null || answer.shortAnswer.trim().length === 0)
    ) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        [questionId]:
          "This field cannot be empty nor can it be just whitespace.",
      }));
      return false;
    }
    return true;
  };

  const handleNext = () => {
    const currentQuestion = questionnaire!.questions[currentQuestionIndex];
    // Validate short answer questions
    if (
      currentQuestion.type === QuestionCategory.ShortAnswer &&
      !validateAnswer(currentQuestion.id)
    )
      return; // Prevent moving to the next question if validation fails

    if (currentQuestionIndex < questionnaire!.questions.length - 1)
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    else handleSubmit();
  };

  const handlePrevious = () => {
    if (currentQuestionIndex > 0)
      setCurrentQuestionIndex(currentQuestionIndex - 1);
  };

  const currentQuestion = questionnaire!.questions[currentQuestionIndex];
  const progress =
    ((currentQuestionIndex + 1) / questionnaire!.questions.length) * 100;

  const handleSubmit = () => {
    APIClient.postQuestionResponse(answers).then(() => {
      navigate("/questionnaire-home");
    });
  };
  return (
    <Container>
      <Typography variant="h4">Questionnaire: {questionnaire!.name}</Typography>
      <Box mt={4}>
        <Typography variant="h6" sx={{ marginBottom: "1vh" }}>
          {currentQuestion.text}
        </Typography>
        <Box mt={2}>
          {currentQuestion.type === QuestionCategory.MultipleChoice ? (
            <RadioGroup
              value={answers[currentQuestionIndex]?.singleOptionId || ""}
              onChange={(e) =>
                handleAnswerChange(
                  currentQuestion.id,
                  QuestionCategory.MultipleChoice,
                  Number(e.target.value),
                  null,
                  null
                )
              }
            >
              {currentQuestion.options?.map((option: QuestionOption) => (
                <FormControlLabel
                  key={option.id}
                  value={option.id}
                  control={<Radio />}
                  label={option.text}
                />
              ))}
            </RadioGroup>
          ) : currentQuestion.type ===
            QuestionCategory.MultipleChoiceSelectAll ? (
            currentQuestion.options?.map(
              (option: QuestionOption, i: number) => (
                <FormControlLabel
                  key={option.id}
                  control={
                    <Checkbox
                      onChange={(e) =>
                        handleAnswerChange(
                          currentQuestion.id,
                          QuestionCategory.MultipleChoiceSelectAll,
                          null,
                          option.id,
                          null
                        )
                      }
                    />
                  }
                  label={option.text}
                />
              )
            )
          ) : (
            <TextField
              variant="outlined"
              fullWidth
              value={answers[currentQuestionIndex]?.shortAnswer || ""}
              onChange={(e) => {
                handleAnswerChange(
                  currentQuestion.id,
                  QuestionCategory.ShortAnswer,
                  null,
                  null,
                  e.target.value
                );
              }}
              error={!!errors[currentQuestion.id]}
              helperText={errors[currentQuestion.id]}
              required={true}
            />
          )}
        </Box>
      </Box>

      <Box mt={4} display="flex" justifyContent="space-between">
        <Button
          variant="contained"
          onClick={handlePrevious}
          disabled={currentQuestionIndex === 0}
        >
          Previous
        </Button>
        <Button variant="contained" onClick={handleNext}>
          Next
        </Button>
      </Box>
      <Box mt={4}>
        <LinearProgress variant="determinate" value={progress} />
        <Typography variant="body2" color="textSecondary">
          {`Progress: ${currentQuestionIndex + 1}/${
            questionnaire!.questions.length
          }`}
        </Typography>
      </Box>
    </Container>
  );
};
