import React, { useState, useEffect } from "react";
import { CircularProgress, Container } from "@mui/material";
import QuestionnaireSelection from "../questionnaire-selection/QuestionnaireSelection";

interface Question {
  id: number;
  text: string;
  options: string[];
}

interface Questionnaire {
  id: number;
  title: string;
  link: string;
  questions?: Question[];
}

const QuestionnaireSelectionContainer: React.FC = () => {
  const [questionnaires, setQuestionnaires] = useState<Questionnaire[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const questionnairData: Questionnaire[] = [
    { id: 1, title: "Customer Satisfaction Survey", link: "/questionnaires/1" },
    { id: 2, title: "Product Feedback", link: "/questionnaires/2" },
    { id: 3, title: "Employee Engagement Survey", link: "/questionnaires/3" },
  ];
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Replace with your API endpoint
        // const response = await fetch("/api/questionnaires");

        // if (!response.ok) {
        //   throw new Error("Failed to fetch questionnaires");
        // }
        // const data = await response.json();

        setQuestionnaires(questionnairData);
      } catch (err: any) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <Container>
        <CircularProgress />
        <p>Loading questionnaires...</p>
      </Container>
    );
  }

  if (error) {
    return (
      <Container>
        <p>Error: {error}</p>
      </Container>
    );
  }

  return (
    <Container>
      <QuestionnaireSelection questionnaires={questionnaires} />
    </Container>
  );
};

export default QuestionnaireSelectionContainer;
