import React, { useState } from "react";
import {
  Container,
  Typography,
  Checkbox,
  RadioGroup,
  Radio,
  FormControlLabel,
  Button,
  LinearProgress,
  Box,
  TextField,
} from "@mui/material";
import { useParams } from "react-router-dom";
import { useQuestionnaires } from "../hooks/useQuestionnaires";
import QuestionCategory from "../types/QuestionCategory";
import { APIClient } from "../../../helpers/APIC";
import { QuestionResponseType } from "../types/QuestionResponseType";
import { useUser } from "../../../types/UserContextProvider";

export const Questionnaire: React.FC = () => {
  const user = useUser().user;
  const { id } = useParams<{ id: string }>();
  const questionnaires = useQuestionnaires();

  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState<QuestionResponseType[]>([]);

  const [errors, setErrors] = useState<Record<number, string>>({});

  // Find the questionnaire using the ID from the URL after ensuring hooks are defined
  const questionnaire = questionnaires.questionnaires.find(
    (q) => q.id === Number(id)
  );
  if (!questionnaire) return;
  <Container>
    <Typography variant="h6">Questionnaire not found</Typography>
  </Container>;

  const handleAnswerChange = (
    questionId: number,
    answerType: QuestionCategory,
    value: string | null,
    singleOptionIndex: number | null,
    multiOptionIndex: number | null,
    multiOptionChecked: boolean | null
  ) => {
    setAnswers((prevAnswers) => {
      const answerIndex = prevAnswers.findIndex(
        (answer) => answer.questionId === questionId
      );

      // Handle multi select questions
      if (
        answerType === QuestionCategory.MultipleChoiceSelectAll &&
        multiOptionChecked !== null &&
        multiOptionIndex !== null
      ) {
        let updateOptionsChecked: boolean[] = [];
        const existingAnswer = prevAnswers[answerIndex];

        // Update curr answer if it exists, else create a new answer
        if (answerIndex > -1 && existingAnswer.optionsChecked != null) {
          for (let i = 0; i < existingAnswer.optionsChecked.length; i++)
            updateOptionsChecked[i] = existingAnswer.optionsChecked[i];
          updateOptionsChecked[multiOptionIndex] = multiOptionChecked;

          const updatedAnswer: QuestionResponseType = {
            ...existingAnswer,
            optionsChecked: updateOptionsChecked,
          };
          // insert the updated answer into the answer array
          return [
            ...prevAnswers.slice(0, answerIndex),
            updatedAnswer,
            ...prevAnswers.slice(answerIndex + 1),
          ];
        } else {
          // Initialize the optionsChecked array
          for (let i = 0; i < currentQuestion.options.length; i++)
            updateOptionsChecked[i] = false;
          updateOptionsChecked[multiOptionIndex] = multiOptionChecked;

          // Add the new answer to the previous answers
          return [
            ...prevAnswers,
            {
              userId: user?.id ?? "",
              questionId,
              shortAnswer: null,
              optionsChecked: updateOptionsChecked,
              optionSelected: null,
              type: answerType,
            },
          ];
        }
      }

      // Handle single select questions
      else if (
        answerType === QuestionCategory.MultipleChoice &&
        singleOptionIndex != null
      ) {
        // If the answer already exists, update the optionSelected value
        if (answerIndex > -1) {
          const updatedAnswer: QuestionResponseType = {
            ...prevAnswers[answerIndex],
            optionSelected: singleOptionIndex,
          };
          return [
            ...prevAnswers.slice(0, answerIndex),
            updatedAnswer,
            ...prevAnswers.slice(answerIndex + 1),
          ];
        } else {
          const updatedAnswer: QuestionResponseType = {
            userId: user?.id ?? "",
            questionId: questionId,
            optionsChecked: [],
            optionSelected: singleOptionIndex,
            shortAnswer: null,
            type: answerType,
          };
          return [...prevAnswers, updatedAnswer];
        }
      } else if (answerType === QuestionCategory.ShortAnswer && value != null) {
        // If the answer already exists, concatenate the new value with the existing one
        if (answerIndex > -1 && prevAnswers[answerIndex].shortAnswer != null) {
          const updatedAnswer: QuestionResponseType = {
            ...prevAnswers[answerIndex],
            shortAnswer: prevAnswers[answerIndex].shortAnswer.concat(value),
          };
          console.log("updatedAnswer", updatedAnswer);
          const test = answers[currentQuestionIndex];
          console.log("test", test);
          const test2 = [...prevAnswers, updatedAnswer];
          console.log("test2", test2);

          return [...prevAnswers, updatedAnswer];
        } else {
          const updatedAnswer: QuestionResponseType = {
            userId: user?.id ?? "",
            questionId: questionId,
            optionsChecked: [],
            optionSelected: -1,
            shortAnswer: value,
            type: answerType,
          };
          return [...prevAnswers, updatedAnswer];
        }
      }
      return prevAnswers;
    });

    // Reset error if there was one
    if (errors[questionId]) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        [questionId]: "",
      }));
    }
  };

  // No empty answers or white space only answers
  const validateAnswer = (questionId: number) => {
    const answer = answers[questionId] || "";
    if (
      answer.type === QuestionCategory.ShortAnswer &&
      answer.shortAnswer != null &&
      answer.shortAnswer.trim().length === 0
    ) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        [questionId]:
          "This field cannot be empty nor can it be just whitespace.",
      }));
      return false;
    }
    return true;
  };

  const handleNext = () => {
    const currentQuestion = questionnaire.questions[currentQuestionIndex];
    // Validate short answer questions
    if (
      currentQuestion.type === QuestionCategory.ShortAnswer &&
      !validateAnswer(currentQuestion.id)
    ) {
      return; // Prevent moving to the next question if validation fails
    }

    if (currentQuestionIndex < questionnaire.questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      // Submit the questionnaire
      handleSubmit();
    }
  };

  const handlePrevious = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  const currentQuestion = questionnaire.questions[currentQuestionIndex];
  const progress =
    ((currentQuestionIndex + 1) / questionnaire.questions.length) * 100;

  const handleSubmit = () => {
    new APIClient().postQuestionResponse(answers);
  };
  return (
    <Container>
      <Typography variant="h4">Questionnaire: {questionnaire.name}</Typography>
      <Box mt={4}>
        <Typography variant="h6" sx={{ marginBottom: "1vh" }}>
          {currentQuestion.text}
        </Typography>
        {/* Add uniform spacing between question text and response area */}
        <Box mt={2}>
          {currentQuestion.type === QuestionCategory.MultipleChoice ? (
            <RadioGroup
              value={answers[currentQuestionIndex] || ""}
              onChange={(e) =>
                handleAnswerChange(
                  currentQuestion.id,
                  QuestionCategory.MultipleChoice,
                  e.target.value,
                  null,
                  null,
                  null
                )
              }
            >
              {currentQuestion.options?.map((option) => (
                <FormControlLabel
                  key={option.id}
                  value={option.text}
                  control={<Radio />}
                  label={option.text}
                />
              ))}
            </RadioGroup>
          ) : currentQuestion.type ===
            QuestionCategory.MultipleChoiceSelectAll ? (
            currentQuestion.options?.map((option, i) => (
              <FormControlLabel
                key={option.id}
                control={
                  <Checkbox
                    checked={
                      answers[currentQuestionIndex]?.optionsChecked?.[i] ??
                      false
                    }
                    onChange={(e) =>
                      handleAnswerChange(
                        currentQuestion.id,
                        QuestionCategory.MultipleChoiceSelectAll,
                        null,
                        null,
                        i,
                        e.target.checked
                      )
                    }
                  />
                }
                label={option.text}
              />
            ))
          ) : (
            <TextField
              variant="outlined"
              fullWidth
              value={answers[currentQuestionIndex]?.shortAnswer || ""}
              onChange={(e) => {
                handleAnswerChange(
                  currentQuestion.id,
                  QuestionCategory.ShortAnswer,
                  e.target.value,
                  null,
                  null,
                  null
                );
              }}
              error={!!errors[currentQuestion.id]}
              helperText={errors[currentQuestion.id]}
              required={true}
            />
          )}
        </Box>
      </Box>

      <Box mt={4} display="flex" justifyContent="space-between">
        <Button
          variant="contained"
          onClick={handlePrevious}
          disabled={currentQuestionIndex === 0}
        >
          Previous
        </Button>
        <Button variant="contained" onClick={handleNext}>
          Next
        </Button>
      </Box>
      <Box mt={4}>
        <LinearProgress variant="determinate" value={progress} />
        <Typography variant="body2" color="textSecondary">
          {`Progress: ${currentQuestionIndex + 1}/${
            questionnaire.questions.length
          }`}
        </Typography>
      </Box>
    </Container>
  );
};
